import React from 'react'
import StripeCheckout from 'react-stripe-checkout';
import axios from 'axios';
const PaymentCheckout = () => {

    const { product, setProduct } = React.useState({
        name: "A single subscription",
        price: 100 * 100,//To convert centes into dollars

    })

    const makePayment = token => {

        console.log("Yes i am triggered");
        const body = {
            token, product
        }

        // Making the Api call
        axios.post("http://localhost:3001/payment", body)
            .then(response => {
                console.log("The respose from api is ", response)
            })
            .catch(err => {
                console.log("The error occured possible cause :", err);
            })

    }

    return (
        <StripeCheckout
            stripeKey={process.env.REACT_APP_STRIPE_PUBLISTABLE_KEY}
            token={makePayment}
            name="Buy Subscription"
            amount={product.price}

        />
    )
}

export default PaymentCheckout
